import React, {useEffect, useState} from "react";
import axios from 'axios';
import './CrudCurso.css';
import Main from "../template/Main";

const InfoCursos = () => {
    const title = 'Cadastro de Cursos'
    const urlAPI = 'http://localhost:5014/api/curso'
    const [data, setData] = useState([]);
    const [lista, setLista] = useState({
        id: 0,
        codCurso: 0,
        nomeCurso: '',
        periodo: ''
    })
}

const InfoAPI = async () => {
    await axios(urlAPI).then(resp => {
        setData(resp.data)
    })
}

export default function CrudCurso() {
    const [curso, setCurso] = useState({
        id: 0,
        codCurso: 0,
        nomeCurso: '',
        periodo: ''
    })


    useEffect(() => {
        InfoAPI();
    })

    return (
        <div className="listagem">
                <table className="listaCursos" id="tblListaCursos">
                    <thead>
                        <tr className="cabecTabela">
                            <th className="tabTituloNome">Ra</th>
                            <th className="tabTituloCod">Nome</th>
                            <th className="tabTituloPeriodo">Curso</th>
                        </tr>
                    </thead>

                    <tbody>
                        {data.map(
                            (curso) =>
                            <tr key={curso.id}>
                                <td>{curso.nomeCurso}</td>
                                <td>{curso.codCurso}</td>
                                <td>{curso.periodo}</td>
                            </tr>
                        )}
                    </tbody>
                </table>
        </div>
    )
}
